data = queries.tooljetdbGetTickets.data;

last3Months = [
  moment().add(-2, "month").format("MMM YYYY"),
  moment().add(-1, "month").format("MMM YYYY"),
  moment().format("MMM YYYY"),
];

ticketStats = {
  generatedTickets: 0,
  inProgressTickets: 0,
  openTickets: 0,
  onHoldTickets: 0,
  resolvedTickets: 0,
  thisMonthTickets: 0,
  thisQuarterTickets: 0,
  chartData: [
    { x: last3Months[0], y: 0 },
    { x: last3Months[1], y: 0 },
    { x: last3Months[2], y: 0 },
  ],
};

data.forEach((ticket) => {
  ticketStats.openTickets += ticket.status == "open" ? 1 : 0;
  ticketStats.onHoldTickets += ticket.status == "on_hold" ? 1 : 0;
  ticketStats.resolvedTickets += ticket.status == "resolved" ? 1 : 0;
  ticketStats.generatedTickets += ticket.status == "ticket_generated" ? 1 : 0;
  ticketStats.inProgressTickets += ticket.status == "in_progress" ? 1 : 0;
  ticketStats.thisMonthTickets +=
    moment(ticket.created_at).format("MM|YYYY") == moment().format("MM|YYYY")
      ? 1
      : 0;
  ticketStats.thisQuarterTickets +=
    moment(ticket.created_at).format("Q|YYYY") == moment().format("Q|YYYY")
      ? 1
      : 0;
  if (last3Months.includes(moment(ticket.created_at).format("MMM YYYY"))) {
    ticketStats.chartData[
      last3Months.indexOf(moment(ticket.created_at).format("MMM YYYY"))
    ].y += 1;
  }
});

return ticketStats;
